const btnHamburger=document.querySelector("#btnHamburger"),header=document.querySelector(".header"),overlay=document.querySelector(".overlay");btnHamburger.addEventListener("click",(function(){header.classList.contains("open")?(header.classList.remove("open"),overlay.classList.remove("fade-in"),overlay.classList.add("fade-out")):(header.classList.add("open"),overlay.classList.remove("fade-out"),overlay.classList.add("fade-in"))}));