const btnHamburger=document.querySelector("#btnHamburger"),body=document.querySelector("body"),header=document.querySelector(".header"),fadeElems=document.querySelectorAll(".has-fade");btnHamburger.addEventListener("click",(function(){header.classList.contains("open")?(header.classList.remove("open"),body.classList.remove("noscroll"),fadeElems.forEach((function(e){e.classList.remove("fade-in"),e.classList.add("fade-out")}))):(body.classList.add("noscroll"),header.classList.add("open"),fadeElems.forEach((function(e){e.classList.remove("fade-out"),e.classList.add("fade-in")})))}));